INSERT INTO `ics_block_information` (`id`, `title`, `description`, `link`, `sort`, `image_path`, `create_datetime`, `update_datetime`, `publish`) VALUES
(1, 'test', '', '#', 1, 'http://www.pwplus.co.th/wp-content/uploads/2014/04/logo.png', '2015-01-05 23:02:30', '2015-01-05 23:02:30', 0),
(2, 'Welcome', 'ในปัจจุบัน รากฟันเทียมได้มีการพัฒนาไปอย่าง มากทั้งในเชิงชีววัสดุ และการออกแบบองค์ประกอบของรากฟันเทียม ส่งผลให้ค่าใช้จ่ายลดลงกว่าในอดีต จึงทำให้วิธีการรักษาด้วยการใส่รากฟันเทียมมีอัตราขยายตัวขึ้นอย่างก้าวกระโดด ทั้งในส่วนของผู้เข้ารับการรักษา และทันตแพทย์', '#', 1, 'http://www.pwplus.co.th/wp-content/uploads/2014/04/home-7.png', '2015-01-05 23:07:10', '2015-01-05 23:58:22', 1),
(3, 'Certificate', 'ผลิตภัณฑ์รากฟันเทียม PW+ ได้รับการรับรองมาตรฐานระดับโลก\r\nEN ISO 13485 : 2003/AC : 2007\r\nMDD 93/42/EEC Annex II (CE 0123)\r\nGMP Ref.No. 1-1-04-17-06-00032', '#', 2, 'http://www.pwplus.co.th/wp-content/uploads/2014/04/flag-7.png', '2015-01-05 23:24:43', '2015-01-05 23:24:43', 1),
(4, 'News/Event', '>> โครงการรากฟันเทียมแก่ผู้ใช้แรงงาน 20,000 คน ฟรี \r\n>> บริการทันตกรรมรากฟันเทียม ฟรี แก่ประชาชน ผู้สูงอายุ ผู้ด้อยโอกาส จำนวน 10,000 ราย ที่มีฟันปลอมทั้งปากหรือสูญเสียฟันทั้งหมด \r\n>> 2/12/53 > รายการเจาะใจ “ด้วยพระบารมี โครงการรากฟันเทียมเฉลิมพระเกียรติ\r\n>> 28/11/53 > รายการดลใจประชาราษฎร์ “รากฟันเทียมเฉลิมพระเกียรติ”', '#', 3, 'http://www.pwplus.co.th/wp-content/uploads/2014/04/calendar-7.png', '2015-01-05 23:28:09', '2015-01-05 23:37:53', 1);
INSERT INTO `ics_block_tab` (`id`, `title`, `description`, `link`, `sort`, `image_path`, `create_datetime`, `update_datetime`, `publish`) VALUES
(1, 'รากฟันเทียมคืออะไร ?', 'รากฟันเทียม เป็นรากฟันที่ทำจากโลหะ ซึ่งนักวิทยาศาสตร์สร้างขึ้นแทนรากฟันธรรมชาติทีสูญเสียไป โดยมากจะทำจากโลหะไททาเนียมเป็นหลัก เพราะได้รับการพิสูจน์แล้วว่า ไม่เป็นอันตรายและเกิดปฏิกิริยาใดๆ รากฟันเทียมจะมีลักษณะคล้ายกับสกรู ในการรักษาแพทย์จะใส่รากฟันเทียมเข้ากับกระดูกขากรรไกรเพื่อใช้เป็นหลักสำหรับยึดฟันปลอมทั้งชนิดที่ถอดได้และชนิดที่ติดแน่น โดยหลังจากการรักษาแล้วผู้ป่วยจะบดเคี้ยวอาหารได้เหมือนปกติ', '#', 1, 'http://www.pwplus.co.th/wp-content/uploads/2014/04/pwplus-002-150x150.png', '2014-04-30 12:19:40', '2014-04-30 12:19:40', 1),
(2, 'เรามีสิทธิเลือกระบบของรากฟันเทียมได้หรือไม่?', 'แม้ว่ารากฟันเทียมนี้ได้ทำการผลิตขึ้นในประเทศไทย แต่ก็มีองค์ประกอบต่างๆ ที่ทำให้รากฟันเทียมมีคุณภาพได้มาตรฐาน และปลอดภัยต่อผู้รับการรักษา', '#', 3, 'http://www.pwplus.co.th/wp-content/uploads/2014/04/pwplus-002-150x150.png', '2014-04-30 12:30:06', '2014-04-30 12:33:45', 1),
(3, 'ผลิตภัณฑ์ของไทยมั่นใจได้แน่หรือ?', 'แม้ว่ารากฟันเทียมนี้ได้ทำการผลิตขึ้นในประเทศไทย แต่ก็มีองค์ประกอบต่างๆ ที่ทำให้รากฟันเทียมมีคุณภาพได้มาตรฐาน และปลอดภัยต่อผู้รับการรักษา', '#', 2, 'http://www.pwplus.co.th/wp-content/uploads/2014/04/pwplus-002-150x150.png', '2014-04-30 12:31:49', '2014-04-30 12:31:49', 1);
